import React from 'react';
import { Table } from 'react-bootstrap';

const RaceList = ({ races, onRaceSelect }) => {
  if (!races || races.length === 0) {
    return <p>Không tìm thấy chặng đua nào.</p>;
  }

  return (
    <div>
      <h4>Kết quả tìm kiếm</h4>
      <Table striped bordered hover className="f1-table">
        <thead>
          <tr>
            <th>STT</th>
            <th>ID</th>
            <th>Tên chặng đua</th>
            <th>Số vòng đua</th>
            <th>Địa điểm</th>
            <th>Trạng thái</th>
          </tr>
        </thead>
        <tbody>
          {races.map((race, index) => (
            <tr 
              key={race.id} 
              onClick={() => onRaceSelect(race)}
              className="clickable-row"
            >
              <td>{index + 1}</td>
              <td>{race.id}</td>
              <td>{race.name}</td>
              <td>{race.laps}</td>
              <td>{race.circuit ? race.circuit.name + ', ' + race.circuit.country : 'N/A'}</td>
              <td>{translateStatus(race.status)}</td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
};

// Helper function to translate race status to Vietnamese
const translateStatus = (status) => {
  const statusMap = {
    'FINISHED': 'Đã kết thúc',
    'ONGOING': 'Đang diễn ra',
    'NOT_STARTED': 'Chưa diễn ra',
    'CANCELLED': 'Đã hủy'
  };

  return statusMap[status] || status;
};

export default RaceList;
