import axios from 'axios';

// Khi sử dụng proxy trong package.json, chỉ cần đường dẫn tương đối
const API_BASE_URL = 'http://localhost:8080/api/race-stages/search?keyword=01';

const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

export default api;
