import React from 'react';
import { Table, Form } from 'react-bootstrap';

const RaceResultsForm = ({ entries, results, onResultChange, totalLaps }) => {
  const handleStatusChange = (driverId, value) => {
    onResultChange(driverId, 'status', value);

    // If status is not FINISHED, clear finish position and set appropriate values
    if (value !== 'FINISHED') {
      onResultChange(driverId, 'finishPosition', null);
      onResultChange(driverId, 'finishTimeOrGap', 'N/A');
    }
  };

  return (
    <div>
      <h4>Kết quả thi đấu</h4>
      <Table striped bordered hover className="f1-table">
        <thead>
          <tr>
            <th>STT</th>
            <th>ID</th>
            <th>Họ và tên</th>
            <th>Đội đua</th>
            <th>Trạng thái</th>
            <th>Thời gian/Khoảng cách</th>
            <th>Số vòng hoàn thành</th>
          </tr>
        </thead>
        <tbody>
          {entries.map((entry, index) => {
            const result = results.find(r => r.driverId === entry.driverId) || {};
            const isFinished = result.status === 'FINISHED';

            return (
              <tr key={entry.id}>
                <td>{index + 1}</td>
                <td>{entry.driverId}</td>
                <td>{entry.driverName || 'Tay đua ' + entry.driverId}</td>
                <td>{entry.teamName || 'Đội ' + entry.teamId}</td>
                <td>
                  <Form.Select
                    value={result.status || 'PENDING'}
                    onChange={(e) => handleStatusChange(entry.driverId, e.target.value)}
                  >
                    <option value="PENDING">Chưa xuất phát</option>
                    <option value="FINISHED">Hoàn thành</option>
                    <option value="DISQUALIFIED">Không hoàn thành</option>
                  </Form.Select>
                </td>
                <td>
                  <Form.Control
                    type="text"
                    placeholder={isFinished ? "hh:mm:ss.ms hoặc +0.123s" : "N/A"}
                    value={result.finishTimeOrGap || ''}
                    onChange={(e) => onResultChange(entry.driverId, 'finishTimeOrGap', e.target.value)}
                    disabled={!isFinished}
                  />
                </td>
                <td>
                  <Form.Control
                    type="number"
                    min="0"
                    max={totalLaps}
                    value={result.lapsCompleted || (isFinished ? totalLaps : 0)}
                    onChange={(e) => onResultChange(entry.driverId, 'lapsCompleted', e.target.value)}
                  />
                </td>
              </tr>
            );
          })}
        </tbody>
      </Table>
    </div>
  );
};

export default RaceResultsForm;
